import pandas as pd

task = pd.read_csv("1003init2.csv")
prompt_en = task['prompt_en'].tolist()  # 创建prompt_en的初始列 表
nums = 0

# 存储符合条件的索引和值
filtered_indices = [(i, sum(res[i])) for i in range(100) if sum(res[i]) <= 10]

# 对符合条件的索引根据 sum(res[i]) 进行升序排序
sorted_indices = sorted(filtered_indices, key=lambda x: x[1])


# 按照排序后的索引执行用户输入逻辑，但保持原顺序
for i, total in sorted_indices:
    print(f"Index: {i}, Sum: {total}")
    print(task.iloc[i]['task_id'])
    print(task.iloc[i]['task'])
    print(task.iloc[i]['prompt_en'])
    nums += 1
    p = input("Enter new prompt_en (or press Enter to keep the original): \n")
    if p != "":
        prompt_en[i] = p  # 按照原索引位置更新prompt_en
    else:
        prompt_en[i] = task.iloc[i]['prompt_en']  # 保留原始的prompt_en

print(f"Total number of indices with sum <= 5: {nums}")

# 将 prompt_en 列更新回 task 数据框
task['prompt_en'] = prompt_en

# 保存修改后的数据到 CSV 文件
task.to_csv("2 1003.csv", index=False)
